+public class JobStreamMetrics implements RemoteStreamMetrics {
+  private static final String NAMESPACE = "zeebe";
+
+  private static final Gauge STREAM_COUNT =
+      Gauge.build()
+          .namespace(NAMESPACE)
+          .name("broker_open_job_stream_count")
